package com.okidokiteam.gouken.kernel;import java.io.File;import com.okidokiteam.gouken.VaultSettings;import org.ops4j.io.FileUtils;/** * Clean Default settings. */public class CleanAnonymousSettings implements VaultSettings {    final private File m_folder;    CleanAnonymousSettings() {        m_folder = getCleanDirectory();    }    public File getWorkingFolder()    {        return m_folder;    }    private File getCleanDirectory()    {        File workDir = new File( "target/gouken" );        FileUtils.delete( workDir );        workDir.mkdirs();        return workDir;    }}