package com.okidokiteam.gouken.kernel;import javax.inject.Inject;import javax.inject.Provider;import com.okidokiteam.gouken.GoukenResolver;import com.okidokiteam.gouken.Vault;import com.okidokiteam.gouken.VaultSettings;/** * */public class CoreVaultProvider<T> implements Provider<Vault<T>> {    private Class<T> m_clazz;    @Inject    GoukenResolver m_resolver;    @Inject    VaultSettings m_settings;    public CoreVaultProvider( Class<T> clazz )    {        m_clazz = clazz;    }    public Vault<T> get()    {        return new CoreVault<T>( m_resolver, m_settings, m_clazz );    }}